import React, { useState, useEffect } from 'react';
import { getTasks, createTask, updateTask, deleteTask } from './api';
import './App.css';

function App() {
  const [tasks, setTasks] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [success, setSuccess] = useState(null);
  const [title, setTitle] = useState('');
  const [description, setDescription] = useState('');
  const [editingId, setEditingId] = useState(null);
  const [editTitle, setEditTitle] = useState('');
  const [editDescription, setEditDescription] = useState('');
  const [filter, setFilter] = useState('all'); // 'all', 'active', 'completed'
  const [searchQuery, setSearchQuery] = useState('');
  const [deleteConfirmId, setDeleteConfirmId] = useState(null);

  // Fetch all tasks on component mount
  useEffect(() => {
    fetchTasks();
  }, []);

  const fetchTasks = async () => {
    try {
      setLoading(true);
      setError(null);
      const data = await getTasks();
      setTasks(data);
    } catch (err) {
      setError(err.message || 'Failed to load tasks');
    } finally {
      setLoading(false);
    }
  };

  const handleAddTask = async (e) => {
    e.preventDefault();
    
    // Form validation
    if (!title.trim()) {
      setError('Task title cannot be empty');
      return;
    }

    try {
      setError(null);
      const newTask = {
        title: title.trim(),
        description: description.trim() || null,
        done: false,
      };
      const createdTask = await createTask(newTask);
      setTasks([...tasks, createdTask]);
      setTitle('');
      setDescription('');
      showSuccess('Task added successfully!');
    } catch (err) {
      setError(err.message || 'Failed to create task');
    }
  };

  const handleToggleDone = async (task) => {
    try {
      setError(null);
      const updatedTask = await updateTask(task.id, {
        ...task,
        done: !task.done,
      });
      setTasks(tasks.map((t) => (t.id === task.id ? updatedTask : t)));
      showSuccess(task.done ? 'Task marked as incomplete!' : 'Task completed!');
    } catch (err) {
      setError(err.message || 'Failed to update task');
    }
  };

  const handleDelete = async (taskId) => {
    try {
      setError(null);
      await deleteTask(taskId);
      setTasks(tasks.filter((t) => t.id !== taskId));
      setDeleteConfirmId(null);
      showSuccess('Task deleted successfully!');
    } catch (err) {
      setError(err.message || 'Failed to delete task');
      setDeleteConfirmId(null);
    }
  };

  const handleDeleteClick = (taskId) => {
    setDeleteConfirmId(taskId);
  };

  const handleCancelDelete = () => {
    setDeleteConfirmId(null);
  };

  const handleStartEdit = (task) => {
    setEditingId(task.id);
    setEditTitle(task.title);
    setEditDescription(task.description || '');
  };

  const handleCancelEdit = () => {
    setEditingId(null);
    setEditTitle('');
    setEditDescription('');
  };

  const handleSaveEdit = async (taskId) => {
    if (!editTitle.trim()) {
      setError('Task title cannot be empty');
      return;
    }

    try {
      setError(null);
      const updatedTask = await updateTask(taskId, {
        title: editTitle.trim(),
        description: editDescription.trim() || null,
        done: tasks.find(t => t.id === taskId).done,
      });
      setTasks(tasks.map((t) => (t.id === taskId ? updatedTask : t)));
      setEditingId(null);
      setEditTitle('');
      setEditDescription('');
      showSuccess('Task updated successfully!');
    } catch (err) {
      setError(err.message || 'Failed to update task');
    }
  };

  const showSuccess = (message) => {
    setSuccess(message);
    setTimeout(() => setSuccess(null), 3000);
  };

  // Filter and search tasks
  const filteredTasks = tasks.filter((task) => {
    // Apply filter
    if (filter === 'active' && task.done) return false;
    if (filter === 'completed' && !task.done) return false;
    
    // Apply search
    if (searchQuery.trim()) {
      const query = searchQuery.toLowerCase();
      return (
        task.title.toLowerCase().includes(query) ||
        (task.description && task.description.toLowerCase().includes(query))
      );
    }
    
    return true;
  });

  // Calculate statistics
  const stats = {
    total: tasks.length,
    active: tasks.filter(t => !t.done).length,
    completed: tasks.filter(t => t.done).length,
  };

  const formatDate = (dateString) => {
    if (!dateString) return '';
    const date = new Date(dateString);
    return date.toLocaleDateString() + ' ' + date.toLocaleTimeString([], { hour: '2-digit', minute: '2-digit' });
  };

  return (
    <div className="App">
      <div className="container">
        <h1>Task Manager</h1>

        {/* Error message */}
        {error && <div className="error-message">{error}</div>}
        
        {/* Success message */}
        {success && <div className="success-message">{success}</div>}

        {/* Add Task Form */}
        <form onSubmit={handleAddTask} className="task-form">
          <div className="form-group">
            <label htmlFor="title">Task Title *</label>
            <input
              type="text"
              id="title"
              value={title}
              onChange={(e) => setTitle(e.target.value)}
              placeholder="Enter task title"
              className="input-field"
            />
          </div>
          <div className="form-group">
            <label htmlFor="description">Description (Optional)</label>
            <input
              type="text"
              id="description"
              value={description}
              onChange={(e) => setDescription(e.target.value)}
              placeholder="Enter task description"
              className="input-field"
            />
          </div>
          <button type="submit" className="add-button">
            Add Task
          </button>
        </form>

        {/* Tasks List */}
        <div className="tasks-section">
          <div className="tasks-header">
            <h2>Tasks</h2>
            {!loading && tasks.length > 0 && (
              <div className="task-stats">
                <span className="stat-item">Total: <strong>{stats.total}</strong></span>
                <span className="stat-item">Active: <strong>{stats.active}</strong></span>
                <span className="stat-item">Completed: <strong>{stats.completed}</strong></span>
              </div>
            )}
          </div>

          {/* Search and Filter */}
          {!loading && tasks.length > 0 && (
            <div className="search-filter-bar">
              <input
                type="text"
                placeholder="Search tasks..."
                value={searchQuery}
                onChange={(e) => setSearchQuery(e.target.value)}
                className="search-input"
              />
              <div className="filter-buttons">
                <button
                  onClick={() => setFilter('all')}
                  className={`filter-btn ${filter === 'all' ? 'active' : ''}`}
                >
                  All
                </button>
                <button
                  onClick={() => setFilter('active')}
                  className={`filter-btn ${filter === 'active' ? 'active' : ''}`}
                >
                  Active
                </button>
                <button
                  onClick={() => setFilter('completed')}
                  className={`filter-btn ${filter === 'completed' ? 'active' : ''}`}
                >
                  Completed
                </button>
              </div>
            </div>
          )}

          {loading ? (
            <div className="loading">Loading tasks...</div>
          ) : tasks.length === 0 ? (
            <div className="no-tasks">
              <div className="empty-state">
                <div className="empty-icon">üìù</div>
                <h3>No tasks yet</h3>
                <p>Get started by adding your first task above!</p>
              </div>
            </div>
          ) : filteredTasks.length === 0 ? (
            <div className="no-tasks">
              <div className="empty-state">
                <div className="empty-icon">üîç</div>
                <h3>No tasks found</h3>
                <p>Try adjusting your search or filter criteria.</p>
              </div>
            </div>
          ) : (
            <ul className="tasks-list">
              {filteredTasks.map((task) => (
                <li key={task.id} className={`task-item ${task.done ? 'done' : ''}`}>
                  {editingId === task.id ? (
                    // Edit mode
                    <div className="task-edit-form">
                      <input
                        type="text"
                        value={editTitle}
                        onChange={(e) => setEditTitle(e.target.value)}
                        className="input-field"
                        placeholder="Task title"
                      />
                      <input
                        type="text"
                        value={editDescription}
                        onChange={(e) => setEditDescription(e.target.value)}
                        className="input-field"
                        placeholder="Description (optional)"
                      />
                      <div className="edit-actions">
                        <button
                          onClick={() => handleSaveEdit(task.id)}
                          className="save-button"
                        >
                          Save
                        </button>
                        <button
                          onClick={handleCancelEdit}
                          className="cancel-button"
                        >
                          Cancel
                        </button>
                      </div>
                    </div>
                  ) : (
                    // View mode
                    <>
                      <div className="task-content">
                        <h3 className="task-title">{task.title}</h3>
                        {task.description && (
                          <p className="task-description">{task.description}</p>
                        )}
                        {task.created_at && (
                          <p className="task-date">Created: {formatDate(task.created_at)}</p>
                        )}
                      </div>
                      <div className="task-actions">
                        <button
                          onClick={() => handleToggleDone(task)}
                          className={`toggle-button ${task.done ? 'done' : 'undo'}`}
                        >
                          {task.done ? 'Undo' : 'Done'}
                        </button>
                        <button
                          onClick={() => handleStartEdit(task)}
                          className="edit-button"
                        >
                          Edit
                        </button>
                        {deleteConfirmId === task.id ? (
                          <div className="delete-confirm">
                            <span>Delete?</span>
                            <button
                              onClick={() => handleDelete(task.id)}
                              className="confirm-delete-button"
                            >
                              Yes
                            </button>
                            <button
                              onClick={handleCancelDelete}
                              className="cancel-delete-button"
                            >
                              No
                            </button>
                          </div>
                        ) : (
                          <button
                            onClick={() => handleDeleteClick(task.id)}
                            className="delete-button"
                          >
                            Delete
                          </button>
                        )}
                      </div>
                    </>
                  )}
                </li>
              ))}
            </ul>
          )}
        </div>
      </div>
    </div>
  );
}

export default App;
